const { userId, message, sessionId } = $input.first().json;
if (!userId || !message) {
  throw new Error('缺少必要参数: userId 或 message');
}
return {
  userId: userId.trim(),
  message: message.trim(),
  sessionId: sessionId || `session_${Date.now()}`,
  timestamp: new Date().toISOString()
};

const userData = $input.all()[0].json;
const projectsData = $input.all()[1].json;
const context = {
  user: {
    id: userData.id,
    name: userData.name,
    email: userData.email,
    preferences: userData.preferences || {}
  },
  projects: projectsData.map(project => ({
    id: project.id,
    name: project.name,
    description: project.description,
    status: project.status,
    knowledgeBase: project.knowledge_base || [],
    tasks: project.tasks || []
  })),
  currentSession: $input.first().json
};
return context;

const context = $input.first().json;
const responses = {
  '你好': '您好！我是AI项目助手，很高兴为您服务。我可以帮您管理项目、查询任务和知识库。',
  'hello': 'Hello! I am your AI project assistant. How can I help you today?',
  '嗨': '嗨！有什么可以帮您的吗？',
  '项目': '让我帮您查看项目信息...您可以说：查看我的项目、创建新项目、项目进度如何',
  'project': 'I can help you with project management. You can ask: Show my projects, Create new project, Project status',
  '任务': '您想了解哪个项目的任务？我可以帮您：查看待办任务、创建新任务、更新任务状态',
  'task': 'Which project tasks would you like to see? I can help you: View pending tasks, Create new tasks, Update task status',
  '知识库': '正在为您查询知识库内容...您可以说：搜索知识库、添加知识条目、学习资料',
  'knowledge': 'Let me search the knowledge base for you. You can ask: Search knowledge, Add knowledge item, Learning materials',
  '帮助': '我可以帮您：项目管理、任务跟踪、知识库查询、智能建议。请告诉我您需要什么帮助？',
  'help': 'I can help you with: Project Management, Task Tracking, Knowledge Base, Smart Suggestions. What do you need help with?',
  'default': `我已经收到您的消息："${context.currentSession.message}"。目前我还在学习阶段，但我会尽力帮助您管理项目信息、跟踪任务进度、查询知识库内容、提供项目建议。您可以说：查看我的项目、创建新任务、知识库搜索、项目进度报告`
};
const lowerMessage = context.currentSession.message.toLowerCase();
let response = responses.default;
for (const [key, value] of Object.entries(responses)) {
  if (lowerMessage.includes(key.toLowerCase()) && key !== 'default') {
    response = value;
    break;
  }
}
if (context.projects && context.projects.length > 0) {
  response += `\n\n您当前有 ${context.projects.length} 个项目：`;
  context.projects.slice(0, 3).forEach(project => {
    response += `\n• ${project.name} (${project.status})`;
  });
  if (context.projects.length > 3) {
    response += `\n• ...还有 ${context.projects.length - 3} 个项目`;
  }
}
return {
  success: true,
  response: {
    message: response,
    sessionId: context.currentSession.sessionId,
    timestamp: new Date().toISOString(),
    context: {
      userId: context.user.id,
      projectCount: context.projects ? context.projects.length : 0
    }
  }
};

const error = $input.first().json;
return {
  success: false,
  error: {
    message: error.message || '处理请求时发生错误',
    code: error.code || 'UNKNOWN_ERROR',
    timestamp: new Date().toISOString()
  }
};
